package leetcode.Monthly.May.FindAnagrams;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class FindAnagrams2 {
    public static void main(String[] args) {
        FindAnagrams2 findAnagrams = new FindAnagrams2();
        findAnagrams.findAnagrams("cbaebabacd", "abc");
        findAnagrams.findAnagrams("abab", "ab");
        findAnagrams.findAnagrams2("baa", "aa");
//        findAnagrams.findAnagrams2(
//                "",
//                "");
//        System.out.println("a" & "b" & "c");

        //s: "cbaebabacd" p: "abc"
    }
    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> output = new ArrayList<>();
        if(p == null){
            return output;
        }
        boolean isAnalgam = false;
        char[] sChar = s.toCharArray();
        for (int i = 0; i < sChar.length - p.length() + 1; i++) {
            isAnalgam = false;
            char[] pChar = p.toCharArray();
            for (int l = 0; l < pChar.length; l++) {
                for (int k = i; k < pChar.length + i; k++) {
                    if (pChar[l] == sChar[k]) {
                        pChar[l] = 0;
                        isAnalgam = true;
                        break;
                    } else {
                        isAnalgam = false;
                    }
                }
                if (!isAnalgam)
                    break;
            }
            if (isAnalgam)
                output.add(i);
        }

        return output;
    }

    public List<Integer> findAnagrams2(String s, String p) {
        List<Integer> output = new ArrayList<>();
        if(p == null){
            return output;
        }
        boolean isAnalgam = false;
        char[] sChar = s.toCharArray();
        char[] pChar = p.toCharArray();
//        Arrays.sort(pChar);
       Map<Integer,Boolean> map = new HashMap<>();
        for (int i = 0; i < sChar.length - pChar.length + 1; i++) {
            isAnalgam = false;
            map.clear();
            for (int l = 0; l < pChar.length; l++) {
                for (int k = i; k < pChar.length + i; k++) {
                    if (pChar[l] == sChar[k] && !map.get(k)) {
                        isAnalgam = true;
                        map.put(k, true);
                        break;
                    } else {
                        isAnalgam = false;
                    }
                }
                if (!isAnalgam)
                    break;
            }
            if (isAnalgam)
                output.add(i);
        }

        return output;
    }
}

